    public void ListVel(){
        veleiro = new Veleiro();
        
        DefaultTableModel modelo = (DefaultTableModel) tblVel.getModel();
        modelo.setRowCount(0);
        
        for(int posLin = 0; posLin < ger.getBdVel().size(); posLin++){
            
            try{
                veleiro.setCodigo(ger.getBdVel().get(posLin).getCodigo());
                veleiro.setNome(ger.getBdVel().get(posLin).getNome());
                veleiro.setTipo(ger.getBdVel().get(posLin).getTipo());
                veleiro.setTripulacao(ger.getBdVel().get(posLin).getTripulacao());
                veleiro.setComprimento(ger.getBdVel().get(posLin).getComprimento());
                veleiro.setMastros(ger.getBdVel().get(posLin).getMastros());
                veleiro.setVelas(ger.getBdVel().get(posLin).getVelas());  
                veleiro.setCapacidade(ger.getBdVel().get(posLin).getCapacidade());
                
                modelo.insertRow(posLin, new Object[]{veleiro.getCodigo(), veleiro.nomeEmbarc(), 
                veleiro.getTipo(), veleiro.getTripulacao(), veleiro.getComprimento(), veleiro.getMastros(),
                veleiro.getVelas(),  veleiro.ingresso(),  veleiro.autonomia()});
            }
            catch(NumNegatException nne){
                 nne.impNNE();                
             }
            catch(BlankStringException bse){
                bse.impBlank();
            }
            catch(SupCapacidade SPC){
                SPC.impSCP();
            }
        }
    }
   
   public void LimparVel(){
        cxVelCod.setText("");
        cxVelNome.setText("");
        cxVelTipo.setText("");
        cxVelTrip.setText("");
        cxVelComp.setText("");
        cxVelMastros.setText("");
        cxVelVelas.setText("");
        cxVelSup.setText("");
        cxVelCod.requestFocus();
   }
   
   public void sair(){
        int resp = JOptionPane.showConfirmDialog(null, "Deseja Realmente sair?", "Saida", JOptionPane.YES_NO_OPTION);
        if(resp == 0)
            dispose();
   }
   
   public void cadVel(){
       
        try{
            veleiro.setCodigo(Integer.parseInt(cxVelCod.getText()));         
            veleiro.setTripulacao(Integer.parseInt(cxVelTrip.getText()));
            veleiro.setComprimento(Double.parseDouble(cxVelComp.getText()));
            veleiro.setMastros(Integer.parseInt(cxVelMastros.getText()));
            veleiro.setVelas(Integer.parseInt(cxVelVelas.getText()));
            veleiro.setCapacidade(Double.parseDouble(cxVelSup.getText()));
            veleiro.setNome(cxVelNome.getText());
            veleiro.setTipo(cxVelTipo.getText());

            veleiro = ger.cadVeleiro(veleiro);

            if(veleiro != null){
                JOptionPane.showMessageDialog(null, "Cadastro confirmado", "Cadastro", JOptionPane.PLAIN_MESSAGE);
            }
            else
                JOptionPane.showMessageDialog(null, "Ja existe alguem com este Veleiro, Cadastro nao executado", "Erro", JOptionPane.ERROR_MESSAGE);    
        }
        catch(NumNegatException nne){
            nne.impNNE();
        }
        catch(NumberFormatException nfe){
            JOptionPane.showMessageDialog(null, "Escreva um numero:", "Erro", JOptionPane.ERROR_MESSAGE);
        }
        catch(SupCapacidade SPC){
            SPC.impSCP();
        }
        catch(BlankStringException bse){
            bse.impBlank();
        }    
   }
    
